import React, { useState } from 'react';

const variables = [
    { name: 'nom', value: 'Alice' },
    { name: 'âge', value: '25' },
    { name: 'ville', value: 'Paris' },
];

export default function VariableIntro() {
    const [quizAnswer, setQuizAnswer] = useState('');
    const [quizResult, setQuizResult] = useState(null);

    const [userPairs, setUserPairs] = useState({});
    const [validationResult, setValidationResult] = useState(null);

    const values = ['Alice', '25', 'Paris'];

    function handleQuizSubmit(e) {
        e.preventDefault();
        if (quizAnswer.trim().toLowerCase() === 'boîte' || quizAnswer.trim().toLowerCase().includes('boîte')) {
            setQuizResult(true);
        } else {
            setQuizResult(false);
        }
    }

    function handlePairChange(variable, value) {
        setUserPairs(prev => ({
            ...prev,
            [variable]: value
        }));
    }

    function checkPairs() {
        const allCorrect = variables.every(v => userPairs[v.name] === v.value);
        setValidationResult(allCorrect);
    }

    return (
        <div className="max-w-xl mx-auto p-6 space-y-8 font-sans">
            <p className="text-lg">
                Une <strong>variable</strong>, c’est comme une boîte avec une étiquette. On peut y mettre une information, comme un prénom, un âge ou un lieu.
            </p>
            {/* Visuel des variables comme des boîtes */}
            <section className="mt-6">
                <h3 className="text-xl font-semibold mb-2">Les variables en image</h3>
                <div className="grid grid-cols-3 gap-4 text-center">
                    {variables.map(v => (
                        <div key={v.name} className="border border-gray-400 rounded-xl p-4 shadow bg-yellow-50">
                            <div className="text-sm text-gray-500 mb-2">Nom de la variable</div>
                            <div className="text-lg font-bold">{v.name}</div>
                            <div className="h-0.5 bg-gray-300 my-2" />
                            <div className="text-sm text-gray-500 mb-1">Valeur contenue</div>
                            <div className="text-blue-600 text-lg">{v.value}</div>
                        </div>
                    ))}
                </div>
            </section>

            <h2 className="text-2xl font-semibold mt-6">Exemple :</h2>
            <pre className="bg-gray-100 p-4 rounded">
{`let nom = "Alice";
let âge = 25;
let ville = "Paris";`}
      </pre>
            <p className="mt-2">Ici, on a trois variables qui contiennent des informations simples.</p>

            {/* Quiz */}
            <section className="mt-8">
                <h3 className="text-xl font-semibold">Quiz rapide</h3>
                <form onSubmit={handleQuizSubmit} className="mt-2 space-y-2">
                    <label className="block">
                        Une variable, c’est comme une... ?
                        <input
                            type="text"
                            className="block border border-gray-400 rounded px-2 py-1 mt-1 w-full"
                            value={quizAnswer}
                            onChange={e => setQuizAnswer(e.target.value)}
                            placeholder="Écris ta réponse ici"
                        />
                    </label>
                    <button
                        type="submit"
                        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
                    >
                        Valider
                    </button>
                </form>
                {quizResult === true && <p className="mt-2 text-green-600 font-semibold">Exactement ! Une boîte qui contient une valeur.</p>}
                {quizResult === false && <p className="mt-2 text-red-600 font-semibold">Essaie avec une image simple :)</p>}
            </section>

            {/* Mini jeu : associer nom → Alice */}
            <section className="mt-8">
                <h3 className="text-xl font-semibold mb-2">Associe chaque variable à sa valeur</h3>
                <div className="space-y-4">
                    {variables.map(v => (
                        <div key={v.name} className="flex items-center space-x-4">
                            <span className="w-24 font-medium">{v.name} :</span>
                            <select
                                value={userPairs[v.name] || ''}
                                onChange={e => handlePairChange(v.name, e.target.value)}
                                className="border border-gray-300 rounded px-2 py-1"
                            >
                                <option value="">-- Choisis une valeur --</option>
                                {values.map(val => (
                                    <option key={val} value={val}>{val}</option>
                                ))}
                            </select>
                        </div>
                    ))}
                </div>
                <button
                    onClick={checkPairs}
                    className="mt-4 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
                >
                    Vérifier les associations
                </button>
                {validationResult === true && <p className="mt-2 text-green-600 font-semibold">Parfait ! Tu maîtrises les variables.</p>}
                {validationResult === false && <p className="mt-2 text-red-600 font-semibold">Hmm, une ou plusieurs associations sont incorrectes.</p>}
            </section>
        </div>
    );
}
