import React, { useState } from 'react';

const levels = [
    {
        id: 1,
        title: "Niveau 1 : Les animaux simples",
        questions: [
            { key: "fly", text: "Peut-il voler ?", options: ["Oui", "Non"] },
            { key: "swim", text: "Peut-il nager ?", options: ["Oui", "Non"] },
            { key: "mammal", text: "Est-ce un mammif√®re ?", options: ["Oui", "Non"] },
        ],
        correct: { fly: "Oui", swim: "Non", mammal: "Non" },
        result: "üê¶ Tu penses √† un oiseau !",
        imageHint: "ü™Ω üí® üå≥"
    },
    {
        id: 2,
        title: "Niveau 2 : Aquatiques et terrestres",
        questions: [
            { key: "legs", text: "A-t-il des pattes ?", options: ["Oui", "Non"] },
            { key: "shell", text: "A-t-il une carapace ?", options: ["Oui", "Non"] },
            { key: "slow", text: "Est-il lent ?", options: ["Oui", "Non"] },
        ],
        correct: { legs: "Oui", shell: "Oui", slow: "Oui" },
        result: "üê¢ Tu pensais √† une tortue !",
        imageHint: "üêö üê¢ üêå"
    },
    {
        id: 3,
        title: "Niveau 3 : Cr√©atures myst√®res",
        questions: [
            { key: "nocturnal", text: "Est-ce un animal nocturne ?", options: ["Oui", "Non"] },
            { key: "wings", text: "A-t-il des ailes ?", options: ["Oui", "Non"] },
            { key: "blood", text: "Boit-il du sang ?", options: ["Oui", "Non"] },
        ],
        correct: { nocturnal: "Oui", wings: "Oui", blood: "Oui" },
        result: "ü¶á C‚Äô√©tait une chauve-souris !",
        imageHint: "üåô ü©∏ üï∏"
    }
];

export default function LogicGameIntro() {
    const [levelIndex, setLevelIndex] = useState(0);
    const [answers, setAnswers] = useState({});
    const [result, setResult] = useState(null);

    const currentLevel = levels[levelIndex];

    function handleChange(key, value) {
        setAnswers(prev => ({ ...prev, [key]: value }));
    }

    function checkResult() {
        const isCorrect = Object.entries(currentLevel.correct).every(
            ([key, val]) => answers[key] === val
        );
        if (isCorrect) {
            setResult(currentLevel.result);
        } else {
            setResult("‚ùå Mauvaise d√©duction ! R√©essaie.");
        }
    }

    function goToNextLevel() {
        setLevelIndex(prev => prev + 1);
        setAnswers({});
        setResult(null);
    }

    return (
        <div className="max-w-xl mx-auto p-6 space-y-6 font-sans">
            <h1 className="text-2xl font-bold">{currentLevel.title}</h1>
            <p className="text-3xl">{currentLevel.imageHint}</p>

            <div className="space-y-4">
                {currentLevel.questions.map(q => (
                    <div key={q.key}>
                        <p>{q.text}</p>
                        <select
                            value={answers[q.key] || ""}
                            onChange={e => handleChange(q.key, e.target.value)}
                            className="border rounded px-2 py-1 w-full"
                        >
                            <option value="">Choisir</option>
                            {q.options.map(opt => (
                                <option key={opt} value={opt}>{opt}</option>
                            ))}
                        </select>
                    </div>
                ))}
            </div>

            <button
                onClick={checkResult}
                className="mt-4 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
            >
                Deviner
            </button>

            {result && (
                <div className="mt-4 text-xl font-semibold">
                    <p className={result.startsWith("‚ùå") ? "text-red-600" : "text-green-700"}>{result}</p>
                    {result !== "‚ùå Mauvaise d√©duction ! R√©essaie." && levelIndex < levels.length - 1 && (
                        <button
                            onClick={goToNextLevel}
                            className="mt-3 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
                        >
                            Niveau suivant ‚Üí
                        </button>
                    )}
                </div>
            )}
        </div>
    );
}
