import React, { useState } from 'react';

const numbers = [3, 12, 5, 7, 0, 8];

function isValid(n) {
    return n >= 1 && n <= 10;
}

export default function InputRangeLesson() {
    const [validBox, setValidBox] = useState([]);
    const [invalidBox, setInvalidBox] = useState([]);
    const [draggedNumber, setDraggedNumber] = useState(null);
    const [resultMessage, setResultMessage] = useState('');

    // Drag handlers
    function handleDragStart(number) {
        setDraggedNumber(number);
    }

    function handleDropValid() {
        if (draggedNumber === null) return;
        // Ne pas ajouter deux fois le même nombre
        if (!validBox.includes(draggedNumber)) {
            setValidBox([...validBox, draggedNumber]);
            setInvalidBox(invalidBox.filter(n => n !== draggedNumber));
            setResultMessage(''); // reset message à chaque déplacement
        }
        setDraggedNumber(null);
    }

    function handleDropInvalid() {
        if (draggedNumber === null) return;
        if (!invalidBox.includes(draggedNumber)) {
            setInvalidBox([...invalidBox, draggedNumber]);
            setValidBox(validBox.filter(n => n !== draggedNumber));
            setResultMessage('');
        }
        setDraggedNumber(null);
    }

    // Vérification du tri
    function handleCheck() {
        // Tous les nombres doivent être classés
        if (validBox.length + invalidBox.length !== numbers.length) {
            setResultMessage('❗️ Tu dois classer tous les nombres.');
            return;
        }
        const allValidCorrect = validBox.every(isValid);
        const allInvalidCorrect = invalidBox.every(n => !isValid(n));
        if (allValidCorrect && allInvalidCorrect) {
            setResultMessage('🎉 Bravo, tous les nombres sont bien triés !');
        } else {
            setResultMessage('❌ Il y a des erreurs dans ton tri, essaie encore.');
        }
    }

    // Pour afficher les nombres non classés (à déplacer)
    const unclassifiedNumbers = numbers.filter(
        n => !validBox.includes(n) && !invalidBox.includes(n)
    );

    return (
        <div className="max-w-md mx-auto p-6 font-sans space-y-6">
            <h2 className="text-2xl font-bold">Trier les nombres entre 1 et 10</h2>
            <p>Fais glisser chaque nombre dans la bonne boîte :</p>

            <div className="flex space-x-4">
                <div
                    onDragOver={e => e.preventDefault()}
                    onDrop={handleDropValid}
                    className="flex-1 min-h-[120px] border-2 border-green-500 rounded p-4 bg-green-50"
                >
                    <h3 className="font-semibold mb-2 text-green-700">Entre 1 et 10</h3>
                    <div className="flex flex-wrap gap-2">
                        {validBox.map(n => (
                            <div
                                key={n}
                                draggable
                                onDragStart={() => handleDragStart(n)}
                                className="cursor-move bg-green-200 px-3 py-1 rounded"
                            >
                                {n}
                            </div>
                        ))}
                    </div>
                </div>

                <div
                    onDragOver={e => e.preventDefault()}
                    onDrop={handleDropInvalid}
                    className="flex-1 min-h-[120px] border-2 border-red-500 rounded p-4 bg-red-50"
                >
                    <h3 className="font-semibold mb-2 text-red-700">Pas entre 1 et 10</h3>
                    <div className="flex flex-wrap gap-2">
                        {invalidBox.map(n => (
                            <div
                                key={n}
                                draggable
                                onDragStart={() => handleDragStart(n)}
                                className="cursor-move bg-red-200 px-3 py-1 rounded"
                            >
                                {n}
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            <div className="border-2 border-gray-400 rounded p-4 bg-gray-100">
                <h3 className="font-semibold mb-2">Nombres à classer</h3>
                <div className="flex flex-wrap gap-2">
                    {unclassifiedNumbers.length > 0 ? (
                        unclassifiedNumbers.map(n => (
                            <div
                                key={n}
                                draggable
                                onDragStart={() => handleDragStart(n)}
                                className="cursor-move bg-gray-300 px-3 py-1 rounded"
                            >
                                {n}
                            </div>
                        ))
                    ) : (
                        <p className="italic text-gray-500">Tous les nombres ont été classés.</p>
                    )}
                </div>
            </div>

            <button
                onClick={handleCheck}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
            >
                Vérifier le tri
            </button>

            {resultMessage && (
                <p
                    className={`mt-4 font-semibold ${
                        resultMessage.includes('Bravo') ? 'text-green-700' : 'text-red-700'
                    }`}
                >
                    {resultMessage}
                </p>
            )}
        </div>
    );
}
