import { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { Input } from "./ui/input";
import { Button } from "./ui/button";
import { useTranslation } from "react-i18next";
import { Menu, X } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export default function Navbar() {
    const { t, i18n } = useTranslation();
    const [menuOpen, setMenuOpen] = useState(false);
    const location = useLocation();

    const navLinks = [
        { to: "/", label: t("Accueil") },
        { to: "/actualites", label: t("Actualités") },
        { to: "/etat-civil", label: t("État Civil") },
        { to: "/demarches", label: t("Démarches") },
        { to: "/fiches-pratiques", label: t("Fiches Pratiques") },
        { to: "/etranger", label: t("Étranger") },
    ];

    const isActive = (path) => location.pathname === path;

    return (
        <header className="sticky top-0 z-50 backdrop-blur-lg bg-white/70 shadow-md border-b">
            <div className="px-4 py-3 flex items-center justify-between gap-4">
                {/* Logo */}
                <div className="flex items-center gap-3">
                    <img src="/images/logo1.png" alt="Logo" className="h-12" />
                </div>

                {/* Desktop Nav */}
                <nav className="hidden md:flex items-center gap-4">
                    {navLinks.map((link) => (
                        <Link
                            key={link.to}
                            to={link.to}
                            className={`px-4 py-2 rounded-md font-medium transition-all ${
                                isActive(link.to)
                                    ? "bg-blue-600 text-white shadow"
                                    : "hover:bg-gray-100 text-gray-800"
                            }`}
                        >
                            {link.label}
                        </Link>
                    ))}
                </nav>

                {/* Search bar */}
                <div className="hidden md:flex w-full max-w-sm">
                    <Input placeholder={t("searchPlaceholder")} />
                </div>

                {/* Language Switch */}
                <div className="hidden md:flex gap-2">
                    <Button onClick={() => i18n.changeLanguage("fr")}>Fr</Button>
                    <Button onClick={() => i18n.changeLanguage("ar")}>ع</Button>
                </div>

                {/* Mobile Menu Button */}
                <button className="md:hidden" onClick={() => setMenuOpen(!menuOpen)}>
                    {menuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
                </button>
            </div>

            {/* Mobile Menu */}
            <AnimatePresence>
                {menuOpen && (
                    <motion.div
                        className="md:hidden px-4 pb-4 flex flex-col gap-3"
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        transition={{ duration: 0.2 }}
                    >
                        {navLinks.map((link) => (
                            <Link
                                key={link.to}
                                to={link.to}
                                onClick={() => setMenuOpen(false)}
                                className={`px-4 py-2 rounded-md transition ${
                                    isActive(link.to)
                                        ? "bg-blue-600 text-white shadow"
                                        : "hover:bg-gray-100 text-gray-800"
                                }`}
                            >
                                {link.label}
                            </Link>
                        ))}
                        <Input placeholder={t("searchPlaceholder")} />
                        <div className="flex gap-2">
                            <Button onClick={() => i18n.changeLanguage("fr")}>Fr</Button>
                            <Button onClick={() => i18n.changeLanguage("ar")}>ع</Button>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>
        </header>
    );
}
