export function Button({ children, variant = "primary", ...props }) {
    const baseStyle = "px-5 py-3 rounded-lg shadow-md transition-all duration-300 transform hover:scale-105";

    const variants = {
        primary: "bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white",
        secondary: "bg-white border text-gray-700 hover:bg-neutral-100",
    };

    return (
        <button {...props} className={`${baseStyle} ${variants[variant]}`}>
            {children}
        </button>
    );
}
