import React, { useState } from 'react';

export default function FirstAlgorithmGame() {
    const [code, setCode] = useState('');
    const [message, setMessage] = useState('');

    function runCode() {
        const lines = code
            .split('\n')
            .map(l => l.trim())
            .filter(l => l.length > 0);

        // Exemple simple de validation
        if (lines.length === 0) {
            setMessage('Veuillez √©crire au moins une commande.');
            return;
        }

        if (lines.includes('avancer()')) {
            setMessage('Commande avanc√©e d√©tect√©e üëç');
        } else {
            setMessage('Aucune commande reconnue.');
        }
    }

    return (
        <div style={{ maxWidth: 400, margin: '20px auto', fontFamily: 'sans-serif' }}>
            <h1>ü§ñ √âcris ton premier algorithme</h1>
            <textarea
                rows={6}
                style={{ width: '100%', fontFamily: 'monospace' }}
                value={code}
                onChange={e => setCode(e.target.value)}
                placeholder="avancer()\ntournerDroite()"
            />
            <button onClick={runCode} style={{ marginTop: 10, padding: '8px 16px' }}>
                Lancer
            </button>
            <div style={{ marginTop: 20, fontWeight: 'bold' }}>{message}</div>
        </div>
    );
}
