                <Link to="/" className="px-4 py-2 rounded-md font-medium hover:bg-gray-100">Accueil</Link>
                <Link to="/actualites" className="px-4 py-2 rounded-md font-medium hover:bg-gray-100">Actualit√©s</Link>
