import React, { useState } from "react";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { Card, CardContent } from "../components/ui/card";
import { useTranslation } from "react-i18next";
import { Link } from "react-router-dom";

const tabs = [
    { key: "actualites", label: "actualites", content: ["Nouvelle CNI", "Service en ligne", "Avis aux citoyens"] },
    { key: "etatCivil", label: "etatCivil", content: ["Acte de naissance", "Certificat de mariage", "D√©c√®s"] },
    { key: "demarches", label: "demarches", content: ["Passeport", "Carte d'identit√©", "Logement"] },
    { key: "fiches", label: "fiches", content: ["Cr√©er un compte Amana", "S'inscrire √† l'universit√©"] },
    { key: "etranger", label: "etranger", content: ["Consulat", "Retour d√©finitif", "Renouvellement passeport"] }
];

export default function HomePage() {
    const { t, i18n } = useTranslation();
    const [activeTab, setActiveTab] = useState("accueil");
    const currentTab = tabs.find((tab) => tab.key === activeTab);

    return (
        <div className={`min-h-screen flex flex-col bg-gray-50 text-gray-900 ${i18n.language === "ar" ? "rtl" : ""}`}>
            {/* HEADER */}
            <header className="bg-white shadow-lg p-6 flex flex-col md:flex-row justify-between items-center gap-6">
                <div className="flex items-center gap-4">
                    <img src="/images/logo1.png" alt="Administration DZ" className="h-20 w-auto"/>
                </div>
                <div className="flex w-full max-w-md border rounded-lg overflow-hidden shadow-sm">
                    <Input placeholder={t("searchPlaceholder")} />
                    <Button className="bg-green-600 text-white">üîç</Button>
                </div>
                <div className="flex gap-3">
                    <Button onClick={() => i18n.changeLanguage("fr")}>Fran√ßais</Button>
                    <Button onClick={() => i18n.changeLanguage("ar")}>ÿßŸÑÿπÿ±ÿ®Ÿäÿ©</Button>
                </div>
            </header>

            {/* NAVIGATION TABS */}
            <nav className="bg-white border-b px-6 py-3 flex flex-wrap gap-3 justify-center">
                <button
                    onClick={() => setActiveTab("accueil")}
                    className={`px-5 py-3 rounded-md text-md font-medium ${activeTab === "accueil" ? "bg-green-700 text-white" : "hover:bg-gray-100"}`}
                >
                    {t("title")}
                </button>
                {tabs.map((tab) => (
                    <button
                        key={tab.key}
                        onClick={() => setActiveTab(tab.key)}
                        className={`px-5 py-3 rounded-md text-md font-medium ${activeTab === tab.key ? "bg-green-700 text-white" : "hover:bg-gray-100"}`}
                    >
                        {t(tab.label)}
                    </button>
                ))}
            </nav>

            {/* SECTION ACCUEIL */}
            {activeTab === "accueil" && (
                <section className="p-8 text-center">
                    <h2 className="text-3xl font-bold text-gray-800 mb-4">{t("welcomeMessage")}</h2>
                    <p className="text-lg text-gray-600 mb-6">{t("welcomeDescription")}</p>
                    <div className="grid grid-cols-2 md:grid-cols-3 gap-6 justify-center">
                        <Card>
                            <CardContent className="text-center">
                                <h3 className="text-xl font-semibold">{t("actualites")}</h3>
                                <Link to="/actualites">
                                    <Button className="mt-4 bg-green-600 hover:bg-green-700 text-white">
                                        {t("explorer")}
                                    </Button>
                                </Link>
                            </CardContent>
                        </Card>
                        {/* R√©p√®te pour les autres sections */}
                    </div>
                </section>
            )}

            {/* MAIN CONTENT */}
            {activeTab !== "accueil" && currentTab && (
                <main className="flex-grow p-8">
                    <h2 className="text-2xl font-semibold mb-6">{t(currentTab.label)}</h2>
                    <ul className="list-disc pl-6 space-y-3 text-gray-700">
                        {currentTab.content.map((item, index) => (
                            <li key={index}>{item}</li>
                        ))}
                    </ul>
                </main>
            )}

            {/* FOOTER */}
            <footer className="text-center text-sm text-gray-600 py-6 border-t bg-white">
                <p>{t("footer")}</p>
            </footer>
        </div>
    );
}
