import { useParams } from 'react-router-dom';

const lessonsData = {
    java: ["Variables", "Conditions", "Boucles", "POO", "Collections"],
    git: ["Init Repo", "Commit", "Branch", "Merge"],
    http: ["Méthodes", "Statuts", "Headers", "Requêtes"],
    spring: ["IOC", "REST", "Security", "JPA"],
    sécurité: ["Cryptographie", "XSS", "CSRF", "HTTPS"],
};

export default function LessonPage() {
    const { theme } = useParams();
    const lessons = lessonsData[theme.toLowerCase()] || [];

    return (
        <div className="min-h-screen bg-gray-50 p-8">
            <h1 className="text-3xl font-bold text-teal-800 mb-6">Leçons : {theme}</h1>

            {lessons.length === 0 ? (
                <p className="text-gray-600">Aucune leçon disponible pour ce thème.</p>
            ) : (
                <ul className="space-y-4">
                    {lessons.map((lesson, index) => (
                        <li
                            key={index}
                            className="bg-white shadow rounded-2xl p-4 hover:shadow-md transition"
                        >
                            <h2 className="text-lg font-semibold text-teal-700">{lesson}</h2>
                            <button className="mt-2 px-4 py-1 bg-teal-500 text-white rounded-full text-sm">
                                Commencer
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
