import React, { useState } from "react";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { Card, CardContent } from "../components/ui/card";
import { useTranslation } from "react-i18next";

const tabs = [
    { key: "actualites", label: "Actualit√©s", content: ["Nouvelle CNI", "Service en ligne", "Avis aux citoyens"] },
    { key: "etatCivil", label: "√âtat civil", content: ["Acte de naissance", "Certificat de mariage", "D√©c√®s"] },
    { key: "demarches", label: "D√©marches", content: ["Passeport", "Carte d'identit√©", "Logement"] },
    { key: "fiches", label: "Fiches pratiques", content: ["Cr√©er un compte Amana", "S'inscrire √† l'universit√©"] },
    { key: "etranger", label: "√âtranger", content: ["Consulat", "Retour d√©finitif", "Renouvellement passeport"] }
];

export default function HomePage() {
    const { t, i18n } = useTranslation();
    const [activeTab, setActiveTab] = useState("accueil");

    const currentTab = tabs.find((tab) => tab.key === activeTab);

    return (
        <div className="min-h-screen flex flex-col bg-gray-50 text-gray-900">
            {/* HEADER */}
            <header className="bg-white shadow-lg p-6 flex flex-col md:flex-row justify-between items-center gap-6">
                <div className="flex items-center gap-4">
                    <img src="/logo.svg" alt="Administration DZ" className="h-12" />
                    <h1 className="text-3xl font-bold text-gray-800">{t("title")}</h1>
                </div>
                <div className="flex w-full max-w-md border rounded-lg overflow-hidden shadow-sm">
                    <Input placeholder={t("searchPlaceholder")} className="flex-grow px-4 py-2 border-gray-300 focus:border-green-500" />
                    <Button className="px-4 py-2 bg-green-600 text-white hover:bg-green-700">üîç</Button>
                </div>
                <div className="flex gap-3">
                    <Button onClick={() => i18n.changeLanguage("fr")} className="px-4 py-2 bg-gray-200 hover:bg-gray-300 rounded-lg">Fran√ßais</Button>
                    <Button onClick={() => i18n.changeLanguage("ar")} className="px-4 py-2 bg-gray-200 hover:bg-gray-300 rounded-lg">ÿßŸÑÿπÿ±ÿ®Ÿäÿ©</Button>
                    <Button onClick={() => i18n.changeLanguage("tz")} className="px-4 py-2 bg-gray-200 hover:bg-gray-300 rounded-lg">‚µú‚¥∞‚µé‚¥∞‚µ£‚µâ‚µñ‚µú</Button>
                </div>
            </header>

            {/* NAVIGATION TABS */}
            <nav className="bg-white border-b border-gray-300 px-6 py-3 flex flex-wrap gap-3 justify-center">
                <button
                    onClick={() => setActiveTab("accueil")}
                    className={`px-5 py-3 rounded-md text-md font-medium transition-all ${
                        activeTab === "accueil" ? "bg-green-700 text-white shadow-md" : "text-gray-800 hover:bg-gray-100"
                    }`}
                >
                    Accueil
                </button>
                {tabs.map((tab) => (
                    <button
                        key={tab.key}
                        onClick={() => setActiveTab(tab.key)}
                        className={`px-5 py-3 rounded-md text-md font-medium transition-all ${
                            activeTab === tab.key ? "bg-green-700 text-white shadow-md" : "text-gray-800 hover:bg-gray-100"
                        }`}
                    >
                        {tab.label}
                    </button>
                ))}
            </nav>

            {/* SECTION ACCUEIL */}
            {activeTab === "accueil" && (
                <section className="p-8 text-center">
                    <h2 className="text-3xl font-bold text-gray-800 mb-4">Bienvenue sur Administration DZ</h2>
                    <p className="text-lg text-gray-600 mb-6">
                        Acc√©dez rapidement aux informations administratives alg√©riennes avec un site intuitif et moderne.
                    </p>
                    <div className="grid grid-cols-2 md:grid-cols-3 gap-6 justify-center">
                        {tabs.map((tab) => (
                            <Card key={tab.key} className="hover:shadow-xl transition-all">
                                <CardContent className="text-center">
                                    <h3 className="text-xl font-semibold text-gray-800">{tab.label}</h3>
                                    <Button onClick={() => setActiveTab(tab.key)} className="mt-4 bg-green-600 hover:bg-green-700 rounded-md">
                                        Explorer
                                    </Button>
                                </CardContent>
                            </Card>
                        ))}
                    </div>
                </section>
            )}

            {/* MAIN CONTENT */}
            {activeTab !== "accueil" && (
                <main className="flex-grow p-8">
                    <h2 className="text-2xl font-semibold text-gray-800 mb-6">{currentTab.label}</h2>
                    <ul className="list-disc pl-6 space-y-3 text-gray-700">
                        {currentTab.content.map((item, index) => (
                            <li key={index} className="transition-all hover:text-green-600">{item}</li>
                        ))}
                    </ul>
                </main>
            )}

            {/* FOOTER */}
            <footer className="text-center text-sm text-gray-600 py-6 border-t bg-white">
                <p>{t("footer")}</p>
            </footer>
        </div>
    );
}
