// data/lessonsData.js
const lessonsData = {
    java: {
        theme: "Java",
        levels: [
            {
                name: "Débutant",
                courses: [
                    { id: 1, title: "Introduction à Java", type: "cours" },
                    { id: 2, title: "Variables et types", type: "cours" },
                    { id: 3, title: "Jeu : Quiz Java", type: "jeu" },
                ],
            },
            {
                name: "Intermédiaire",
                courses: [
                    { id: 4, title: "POO en Java", type: "cours" },
                    { id: 5, title: "Exceptions", type: "cours" },
                ],
            },
        ],
    },

    git: {
        theme: "Git",
        levels: [
            {
                name: "Débutant",
                courses: [
                    { id: 1, title: "Qu'est-ce que Git ?", type: "cours" },
                    { id: 2, title: "Git init / Git add", type: "cours" },
                ],
            },
        ],
    },

    // Tu peux ajouter HTTP, Spring, etc. de la même manière
};

export default lessonsData;
