import React, { useState } from 'react';

const weatherConditions = [
    { id: 1, condition: 'Il pleut', action: 'Prendre un parapluie' },
    { id: 2, condition: 'Il fait soleil', action: 'Mettre des lunettes de soleil' },
    { id: 3, condition: 'Il fait froid', action: 'Mettre un manteau' },
];

export default function ConditionIntro() {
    const [selectedCondition, setSelectedCondition] = useState('');
    const [quizResult, setQuizResult] = useState(null);

    // Quiz validation
    function handleQuizSubmit(e) {
        e.preventDefault();
        if (
            selectedCondition === 'Il pleut'
        ) {
            setQuizResult(true);
        } else {
            setQuizResult(false);
        }
    }

    return (
        <div className="max-w-xl mx-auto p-6 space-y-8 font-sans">
            <p className="text-lg">
                Une condition dans un algorithme, c’est une question qui guide les actions à faire. Par exemple :
                <strong>Si</strong> il pleut, <strong>alors</strong> je prends un parapluie.
            </p>

            <h2 className="text-2xl font-semibold mt-6">Exemple : Que faire selon la météo ?</h2>
            <ul className="list-disc list-inside space-y-1">
                {weatherConditions.map(({ id, condition, action }) => (
                    <li key={id}>
                        {condition} → {action}
                    </li>
                ))}
            </ul>

            {/* Mini quiz */}
            <section className="mt-8">
                <h3 className="text-xl font-semibold">Quiz rapide</h3>
                <form onSubmit={handleQuizSubmit} className="mt-2 space-y-2">
                    <label className="block">
                        Si « Il pleut », que dois-tu faire ?
                        <select
                            className="block border border-gray-400 rounded px-2 py-1 mt-1 w-full"
                            value={selectedCondition}
                            onChange={(e) => setSelectedCondition(e.target.value)}
                        >
                            <option value="">-- Choisis une réponse --</option>
                            <option value="Il pleut">Prendre un parapluie</option>
                            <option value="Il fait soleil">Mettre des lunettes de soleil</option>
                            <option value="Il fait froid">Mettre un manteau</option>
                        </select>
                    </label>
                    <button
                        type="submit"
                        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
                    >
                        Valider
                    </button>
                </form>
                {quizResult === true && <p className="mt-2 text-green-600 font-semibold">Bravo ! Bonne réponse.</p>}
                {quizResult === false && <p className="mt-2 text-red-600 font-semibold">Essaie encore :)</p>}
            </section>

            {/* Mini jeu interactif */}
            <section className="mt-8">
                <h3 className="text-xl font-semibold mb-2">Associe la bonne action à la météo</h3>
                <p className="mb-4">Clique sur la météo, puis choisis la bonne action :</p>
                <ul className="space-y-3">
                    {weatherConditions.map(({ id, condition, action }) => (
                        <li key={id} className="border border-gray-300 rounded px-4 py-2 bg-gray-50 cursor-pointer hover:bg-gray-100"
                            onClick={() => alert(`Si "${condition}", tu dois : ${action}`)}
                        >
                            {condition}
                        </li>
                    ))}
                </ul>
            </section>
        </div>
    );
}
