import { useState } from "react";
import { Link } from "react-router-dom";
import { Input } from "./ui/input";
import { Button } from "./ui/button";
import { useTranslation } from "react-i18next";
import { Menu, X } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export default function Navbar() {
    const { t, i18n } = useTranslation();
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => setMenuOpen(!menuOpen);

    const navLinks = [
        { to: "/", label: t("Accueil") },
        { to: "/actualites", label: t("Actualités") },
        { to: "/etat-civil", label: t("État Civil") },
        { to: "/demarches", label: t("Démarches") },
        { to: "/fiches-pratiques", label: t("Fiches Pratiques") },
        { to: "/etranger", label: t("Étranger") },
    ];

    return (
        <header className="bg-white shadow-md px-4 py-3">
            <div className="flex items-center justify-between gap-4">
                {/* Logo */}
                <div className="flex items-center gap-3">
                    <img src="/images/logo1.png" alt="Logo" className="h-12" />
                </div>

                {/* Desktop Nav */}
                <nav className="hidden md:flex items-center gap-4">
                    {navLinks.map((link) => (
                        <Link
                            key={link.to}
                            to={link.to}
                            className="px-4 py-2 rounded-md hover:bg-gray-100 transition"
                        >
                            {link.label}
                        </Link>
                    ))}
                </nav>

                {/* Search bar */}
                <div className="hidden md:flex w-full max-w-sm border rounded-lg overflow-hidden shadow-sm">
                    <Input placeholder={t("searchPlaceholder")} />
                    <Button className="bg-green-600 text-white">🔍</Button>
                </div>

                {/* Language Switch */}
                <div className="hidden md:flex gap-2">
                    <Button onClick={() => i18n.changeLanguage("fr")}>Fr</Button>
                    <Button onClick={() => i18n.changeLanguage("ar")}>ع</Button>
                </div>

                {/* Mobile Menu Button */}
                <button className="md:hidden" onClick={toggleMenu}>
                    {menuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
                </button>
            </div>

            {/* Mobile Menu */}
            <AnimatePresence>
                {menuOpen && (
                    <motion.div
                        className="mt-3 md:hidden flex flex-col gap-2"
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -10 }}
                        transition={{ duration: 0.2 }}
                    >
                        {navLinks.map((link) => (
                            <Link
                                key={link.to}
                                to={link.to}
                                className="px-4 py-2 rounded-md hover:bg-gray-100 transition"
                                onClick={() => setMenuOpen(false)}
                            >
                                {link.label}
                            </Link>
                        ))}
                        <div className="flex w-full border rounded-lg overflow-hidden shadow-sm">
                            <Input placeholder={t("searchPlaceholder")} />
                            <Button className="bg-green-600 text-white">🔍</Button>
                        </div>
                        <div className="flex gap-2 justify-center">
                            <Button onClick={() => i18n.changeLanguage("fr")}>Fr</Button>
                            <Button onClick={() => i18n.changeLanguage("ar")}>ع</Button>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>
        </header>
    );
}
