export function Button({ children, ...props }) {
    return (
        <button
            {...props}
            className="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white px-5 py-3 rounded-lg shadow-md transition-all duration-300 transform hover:scale-105"
        >
            {children}
        </button>
    );
}
