import React from "react";
import { useTranslation } from "react-i18next";
const express = require("express");
const axios = require("axios");
const cheerio = require("cheerio");
const cors = require("cors");
export default function EtatCivilPage() {
    const { t } = useTranslation();
    const app = express();
    app.use(cors());

    app.get("/scrape", async (req, res) => {
        try {
            const url = "https://www.interieur.gov.dz/index.php/fr/mes-d%C3%A9marches-administratives/etat-civil/acte-de-naissance.html";
            const { data } = await axios.get(url);
            const $ = cheerio.load(data);

            let result = [];

            $(".content_wrap p").each((index, element) => {
                result.push($(element).text().trim());
            });

            res.json({ info: result });
        } catch (error) {
            console.error("Erreur lors du scraping :", error);
            res.status(500).json({ error: "Impossible de récupérer les données" });
        }
    });

    app.listen(5000, () => {
        console.log("Serveur en cours d'exécution sur http://localhost:5000");
    });
    return (
        <main className="p-8">
            <h2 className="text-2xl font-bold mb-4">{t("etatCivil")}</h2>
            <ul className="list-disc pl-6 space-y-2 text-gray-700">
                <li>{t("birthCertificate")}</li>
                <li>{t("marriageCertificate")}</li>
                <li>{t("deathCertificate")}</li>
            </ul>
        </main>
    );
}
