import React, { useState } from "react";
import { Card, CardContent } from "../components/ui/card";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { useTranslation } from "react-i18next";

const sections = [
    { key: "actualites", title: "Actualités", content: ["Nouvelle carte d'identité", "Rendez-vous en ligne", "Changement d'adresse"] },
    { key: "etatCivil", title: "État civil", content: ["Acte de naissance", "Livret de famille", "Acte de mariage"] },
    { key: "fichesPratiques", title: "Fiches pratiques", content: ["Comment obtenir un passeport ?", "Créer un compte Amana"] },
    { key: "demarches", title: "Démarches", content: ["Demande de logement", "Retrait de permis", "CNAS"] },
    { key: "etranger", title: "Étranger", content: ["Consulat numérique", "Renouvellement passeport", "Retour définitif"] }
];

export default function HomePage() {
    const { t, i18n } = useTranslation();
    const [expandedSection, setExpandedSection] = useState(null);

    const toggleSection = (key) => {
        setExpandedSection(prev => (prev === key ? null : key));
    };

    return (
        <div className="flex flex-col min-h-screen bg-gray-50 text-gray-900 font-sans">
            {/* HEADER */}
            <header className="bg-white shadow-sm py-4 px-6 flex flex-col md:flex-row justify-between items-center gap-4">
                <div className="flex items-center gap-4">
                    <img src="/logo.svg" alt="Administration DZ" className="h-10" />
                    <h1 className="text-2xl font-bold text-blue-800">{t("title")}</h1>
                </div>
                <div className="flex w-full max-w-md">
                    <Input placeholder={t("searchPlaceholder")} className="rounded-l" />
                    <Button className="rounded-r">🔍</Button>
                </div>
                <div className="flex gap-2">
                    <Button onClick={() => i18n.changeLanguage("fr")}>Français</Button>
                    <Button onClick={() => i18n.changeLanguage("ar")}>العربية</Button>
                </div>
            </header>

            {/* MAIN HORIZONTAL SECTIONS */}
            <main className="flex-grow py-6 px-4">
                <h2 className="text-xl font-semibold text-blue-800 mb-4">{t("horizontalSections")}</h2>
                <div className="flex overflow-x-auto gap-4 pb-4">
                    {sections.map(({ key, title, content }) => (
                        <Card
                            key={key}
                            className="min-w-[300px] max-w-[320px] bg-white shadow-sm border rounded-xl"
                        >
                            <button
                                onClick={() => toggleSection(key)}
                                className="text-left w-full px-4 py-3 font-semibold text-blue-800 hover:bg-blue-50 transition"
                            >
                                {expandedSection === key ? "🔽" : "▶️"} {title}
                            </button>
                            {expandedSection === key && (
                                <CardContent className="px-4 pb-4 pt-1 text-sm text-gray-700">
                                    <ul className="list-disc pl-5 space-y-1">
                                        {content.map((item, index) => (
                                            <li key={index}>{item}</li>
                                        ))}
                                    </ul>
                                </CardContent>
                            )}
                        </Card>
                    ))}
                </div>
            </main>

            {/* FOOTER */}
            <footer className="text-center text-sm text-gray-500 py-6 border-t bg-white">
                <p>{t("footer")}</p>
            </footer>
        </div>
    );
}
