import React, { useState } from "react";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { Card, CardContent } from "../components/ui/card";
import { useTranslation } from "react-i18next";
import Navbar from "../components/Navbar";

const tabs = [
    { key: "actualites", label: "Actualités", content: ["Nouvelle CNI", "Service en ligne", "Avis aux citoyens"] },
    { key: "etatCivil", label: "État Civil", content: ["Acte de naissance", "Certificat de mariage", "Décès"] },
    { key: "demarches", label: "Démarches", content: ["Passeport", "Carte d'identité", "Logement"] },
    { key: "fiches", label: "Fiches Pratiques", content: ["Créer un compte Amana", "S'inscrire à l'université"] },
    { key: "etranger", label: "Étranger", content: ["Consulat", "Retour définitif", "Renouvellement passeport"] }
];

export default function HomePage() {
    const { t, i18n } = useTranslation();
    const [activeTab, setActiveTab] = useState("accueil");
    const currentTab = tabs.find((tab) => tab.key === activeTab);

    return (
        <div className={`min-h-screen flex flex-col bg-gray-100 text-gray-900 ${i18n.language === "ar" ? "rtl" : ""}`}>

            {/* HEADER */}
            <header className="bg-white shadow-md p-6 flex flex-col md:flex-row justify-between items-center gap-6">
                <div className="flex items-center gap-4">
                    <img src="/images/logo1.png" alt="Administration DZ" className="h-16 w-auto" />
                    <h1 className="text-xl font-bold text-gray-800">Administration DZ</h1>
                </div>
                <div className="flex w-full max-w-md border rounded-lg overflow-hidden shadow-sm">
                    <Input placeholder={t("Rechercher un service...")} className="w-full px-4 py-2" />
                    <Button className="bg-blue-600 text-white px-4">🔍</Button>
                </div>
                <div className="flex gap-3">
                    <Button onClick={() => i18n.changeLanguage("fr")}>Français</Button>
                    <Button onClick={() => i18n.changeLanguage("ar")}>العربية</Button>
                </div>
            </header>

            {/* SECTION PRINCIPALE */}
            <main className="flex-grow p-8">
                <h2 className="text-2xl font-semibold text-gray-800 mb-6">{t("Bienvenue sur Administration DZ")}</h2>
                <p className="text-lg text-gray-600 mb-6">{t("Simplifiez vos démarches administratives en un clic.")}</p>

                {/* Cartes de services */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {tabs.map((tab) => (
                        <Card key={tab.key} className="shadow-md">
                            <CardContent className="text-center p-6">
                                <h3 className="text-lg font-semibold text-gray-700">{t(tab.label)}</h3>
                                <ul className="list-disc pl-6 mt-3 text-gray-600">
                                    {tab.content.map((item, index) => (
                                        <li key={index}>{item}</li>
                                    ))}
                                </ul>
                                <Button className="mt-4 bg-blue-600 hover:bg-blue-700 text-white">
                                    {t("Explorer")}
                                </Button>
                            </CardContent>
                        </Card>
                    ))}
                </div>
            </main>

            {/* FOOTER */}
            <footer className="text-center text-sm text-gray-600 py-6 border-t bg-white">
                <p>{t("© 2025 Administration DZ – Tous droits réservés")}</p>
            </footer>
        </div>
    );
}
