import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function HomePage() {
  const navigate = useNavigate();

  return (
      <div className="min-h-screen bg-gradient-to-br from-teal-100 to-purple-100 p-8 text-center">
          <h1 className="text-5xl font-bold text-teal-800 mb-4">Apprends en t'amusant</h1>
          <p className="text-lg text-gray-700 mb-6">
              Maîtrise Git, HTTP, Java, Spring, Réseaux et plus grâce à des mini-jeux et leçons interactives !
          </p>
          <button onClick={() => navigate('/dashboard')}
                  className="text-lg px-6 py-3 bg-teal-600 hover:bg-teal-700 text-white rounded-2xl shadow-xl">
              Aller au tableau de bord
          </button>
          <div className="bg-red-500 text-white p-10">
              Si ce bloc est rouge, Tailwind fonctionne !
          </div>
      </div>

  );
}
