import React, { useState } from 'react';

export default function PrixTTC() {
    const [prixHT, setPrixHT] = useState('');
    const [tva, setTva] = useState(20); // TVA en %
    const [prixTTC, setPrixTTC] = useState(null);

    function calculerTTC() {
        const ht = parseFloat(prixHT);
        if (isNaN(ht) || ht < 0) {
            alert('Veuillez entrer un prix HT valide (nombre positif)');
            return;
        }
        const tvaDecimal = tva / 100;
        const ttc = ht * (1 + tvaDecimal);
        setPrixTTC(ttc.toFixed(2));
    }

    return (
        <div style={{ maxWidth: 350, margin: '30px auto', fontFamily: 'Arial, sans-serif' }}>
            <h2>Calculateur Prix HT / TTC</h2>
            <label>
                Prix HT (€) :<br />
                <input
                    type="number"
                    value={prixHT}
                    onChange={e => setPrixHT(e.target.value)}
                    placeholder="Exemple : 100"
                    style={{ padding: '6px 8px', marginTop: 6, width: '100%', boxSizing: 'border-box' }}
                />
            </label>

            <label style={{ display: 'block', marginTop: 12 }}>
                TVA (%) :<br />
                <input
                    type="number"
                    value={tva}
                    onChange={e => setTva(e.target.value)}
                    style={{ padding: '6px 8px', marginTop: 6, width: '100%', boxSizing: 'border-box' }}
                />
            </label>

            <button
                onClick={calculerTTC}
                style={{
                    marginTop: 15,
                    backgroundColor: '#007bff',
                    color: 'white',
                    border: 'none',
                    padding: '10px 15px',
                    borderRadius: 5,
                    cursor: 'pointer',
                    width: '100%',
                    fontSize: 16,
                }}
            >
                Calculer le prix TTC
            </button>

            {prixTTC !== null && (
                <p style={{ marginTop: 20, fontWeight: 'bold' }}>
                    Prix TTC : {prixTTC} €
                </p>
            )}

            <div style={{ marginTop: 25, fontSize: 14, lineHeight: 1.5 }}>
                <h3>Comment ça marche ?</h3>
                <p>
                    On prend le prix HT, puis on ajoute la TVA en multipliant le prix HT par le taux de TVA (exprimé en décimal).<br />
                    Formule : <code>Prix TTC = Prix HT × (1 + TVA)</code>
                </p>
            </div>
        </div>
    );
}
