import React, { useState } from "react";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { useTranslation } from "react-i18next";

const tabs = [
    { key: "actualites", label: "Actualit√©s", content: ["Nouvelle CNI", "Service en ligne", "Avis aux citoyens"] },
    { key: "etatCivil", label: "√âtat civil", content: ["Acte de naissance", "Certificat de mariage", "D√©c√®s"] },
    { key: "demarches", label: "D√©marches", content: ["Passeport", "Carte d'identit√©", "Logement"] },
    { key: "fiches", label: "Fiches pratiques", content: ["Cr√©er un compte Amana", "S'inscrire √† l'universit√©"] },
    { key: "etranger", label: "√âtranger", content: ["Consulat", "Retour d√©finitif", "Renouvellement passeport"] }
];

export default function HomePage() {
    const { t, i18n } = useTranslation();
    const [activeTab, setActiveTab] = useState("actualites");

    const currentTab = tabs.find((tab) => tab.key === activeTab);

    return (
        <div className="min-h-screen flex flex-col bg-gray-50 text-gray-900">
            {/* HEADER */}
            <header className="bg-white shadow-sm p-4 flex flex-col md:flex-row justify-between items-center gap-4">
                <div className="flex items-center gap-4">
                    <img src="/logo.svg" alt="Administration DZ" className="h-10" />
                    <h1 className="text-2xl font-bold text-blue-800">{t("title")}</h1>
                </div>
                <div className="flex w-full max-w-md">
                    <Input placeholder={t("searchPlaceholder")} className="rounded-l" />
                    <Button className="rounded-r">üîç</Button>
                </div>
                <div className="flex gap-2">
                    <Button onClick={() => i18n.changeLanguage("fr")}>Fran√ßais</Button>
                    <Button onClick={() => i18n.changeLanguage("ar")}>ÿßŸÑÿπÿ±ÿ®Ÿäÿ©</Button>
                </div>
            </header>

            {/* NAVIGATION TABS */}
            <nav className="bg-blue-50 border-b border-blue-200 px-4 py-2 flex flex-wrap gap-2 justify-center">
                {tabs.map((tab) => (
                    <button
                        key={tab.key}
                        onClick={() => setActiveTab(tab.key)}
                        className={`px-4 py-2 rounded-md text-sm font-medium ${
                            activeTab === tab.key
                                ? "bg-blue-700 text-white"
                                : "text-blue-700 hover:bg-blue-100"
                        }`}
                    >
                        {tab.label}
                    </button>
                ))}
            </nav>

            {/* MAIN CONTENT */}
            <main className="flex-grow p-6">
                <h2 className="text-xl font-semibold text-blue-800 mb-4">
                    {currentTab.label}
                </h2>
                <ul className="list-disc pl-6 space-y-2 text-gray-700">
                    {currentTab.content.map((item, index) => (
                        <li key={index}>{item}</li>
                    ))}
                </ul>
            </main>

            {/* FOOTER */}
            <footer className="text-center text-sm text-gray-500 py-6 border-t bg-white">
                <p>{t("footer")}</p>
            </footer>
        </div>
    );
}
