import React, { useState } from 'react';

const tauxTVAOptions = [
    { label: '5.5%', value: 5.5 },
    { label: '10%', value: 10 },
    { label: '20%', value: 20 },
];

const quizQuestions = [
    {
        question: 'Si le prix HT est 100 € et la TVA 20%, quel est le prix TTC ?',
        answer: 120,
    },
    {
        question: 'Si le prix TTC est 110 € et la TVA 10%, quel est le prix HT ?',
        answer: 100,
    },
    {
        question: 'Si le prix HT est 50 € et la TVA 5.5%, quel est le prix TTC ?',
        answer: 52.75,
    },
];

export default function PrixHTTTC() {
    const [mode, setMode] = useState('htToTtc'); // 'htToTtc' ou 'ttcToHt'
    const [prix, setPrix] = useState('');
    const [tva, setTva] = useState(20);
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');
    const [quizStep, setQuizStep] = useState(0);
    const [quizAnswer, setQuizAnswer] = useState('');
    const [quizResult, setQuizResult] = useState(null);

    function calculer() {
        setError('');
        const p = parseFloat(prix.replace(',', '.'));
        if (isNaN(p) || p < 0) {
            setError('Veuillez entrer un prix valide (nombre positif)');
            setResult(null);
            return;
        }
        const taux = tva / 100;

        if (mode === 'htToTtc') {
            const ttc = p * (1 + taux);
            setResult(ttc.toFixed(2));
        } else {
            // TTC -> HT = TTC / (1 + taux)
            const ht = p / (1 + taux);
            setResult(ht.toFixed(2));
        }
    }

    function reset() {
        setPrix('');
        setResult(null);
        setError('');
        setQuizAnswer('');
        setQuizResult(null);
        setQuizStep(0);
    }

    function handleQuizSubmit(e) {
        e.preventDefault();
        const correctAnswer = quizQuestions[quizStep].answer;
        const userAnswer = parseFloat(quizAnswer.replace(',', '.'));
        if (isNaN(userAnswer)) {
            setQuizResult('Veuillez entrer un nombre valide.');
            return;
        }
        if (Math.abs(userAnswer - correctAnswer) < 0.01) {
            setQuizResult('Bravo, bonne réponse !');
        } else {
            setQuizResult(`Incorrect, la bonne réponse est ${correctAnswer} €.`);
        }
    }

    function nextQuiz() {
        setQuizStep((quizStep + 1) % quizQuestions.length);
        setQuizAnswer('');
        setQuizResult(null);
    }

    return (
        <div style={{ maxWidth: 400, margin: '30px auto', fontFamily: 'Arial, sans-serif' }}>
            <h2>Calcul Prix HT & TTC</h2>

            <div style={{ marginBottom: 15 }}>
                <button
                    onClick={() => {
                        setMode('htToTtc');
                        reset();
                    }}
                    style={{
                        backgroundColor: mode === 'htToTtc' ? '#007bff' : '#ccc',
                        color: mode === 'htToTtc' ? 'white' : 'black',
                        border: 'none',
                        padding: '8px 12px',
                        marginRight: 8,
                        borderRadius: 4,
                        cursor: 'pointer',
                    }}
                >
                    HT → TTC
                </button>
                <button
                    onClick={() => {
                        setMode('ttcToHt');
                        reset();
                    }}
                    style={{
                        backgroundColor: mode === 'ttcToHt' ? '#007bff' : '#ccc',
                        color: mode === 'ttcToHt' ? 'white' : 'black',
                        border: 'none',
                        padding: '8px 12px',
                        borderRadius: 4,
                        cursor: 'pointer',
                    }}
                >
                    TTC → HT
                </button>
            </div>

            <label>
                {mode === 'htToTtc' ? 'Prix HT (€)' : 'Prix TTC (€)'} :<br />
                <input
                    type="text"
                    value={prix}
                    onChange={(e) => setPrix(e.target.value)}
                    placeholder="Ex: 100"
                    style={{ width: '100%', padding: '8px', marginTop: 6, boxSizing: 'border-box' }}
                />
            </label>

            <label style={{ display: 'block', marginTop: 12 }}>
                TVA :<br />
                <select
                    value={tva}
                    onChange={(e) => setTva(parseFloat(e.target.value))}
                    style={{ width: '100%', padding: '8px', marginTop: 6 }}
                >
                    {tauxTVAOptions.map(({ label, value }) => (
                        <option key={value} value={value}>
                            {label}
                        </option>
                    ))}
                </select>
            </label>

            <button
                onClick={calculer}
                style={{
                    marginTop: 15,
                    backgroundColor: '#007bff',
                    color: 'white',
                    border: 'none',
                    padding: '10px 15px',
                    borderRadius: 5,
                    cursor: 'pointer',
                    width: '100%',
                    fontSize: 16,
                }}
            >
                Calculer
            </button>

            {error && (
                <p style={{ color: 'red', marginTop: 10 }}>
                    {error}
                </p>
            )}

            {result !== null && !error && (
                <p style={{ marginTop: 20, fontWeight: 'bold' }}>
                    Résultat : {result} €
                </p>
            )}

            <hr style={{ margin: '30px 0' }} />

            <h3>Quiz : Testez vos connaissances</h3>
            <p>{quizQuestions[quizStep].question}</p>
            <form onSubmit={handleQuizSubmit}>
                <input
                    type="text"
                    value={quizAnswer}
                    onChange={e => setQuizAnswer(e.target.value)}
                    placeholder="Votre réponse en €"
                    style={{ width: '100%', padding: '8px', marginBottom: 8, boxSizing: 'border-box' }}
                />
                <button
                    type="submit"
                    style={{
                        backgroundColor: '#28a745',
                        color: 'white',
                        border: 'none',
                        padding: '10px 15px',
                        borderRadius: 5,
                        cursor: 'pointer',
                        width: '100%',
                        fontSize: 16,
                    }}
                >
                    Valider
                </button>
            </form>

            {quizResult && (
                <p style={{ marginTop: 10, fontWeight: 'bold', color: quizResult.startsWith('Bravo') ? 'green' : 'red' }}>
                    {quizResult}
                </p>
            )}

            {quizResult && (
                <button
                    onClick={nextQuiz}
                    style={{
                        marginTop: 10,
                        backgroundColor: '#17a2b8',
                        color: 'white',
                        border: 'none',
                        padding: '8px 12px',
                        borderRadius: 5,
                        cursor: 'pointer',
                        width: '100%',
                        fontSize: 14,
                    }}
                >
                    Question suivante
                </button>
            )}

            <div style={{ marginTop: 30, fontSize: 14, lineHeight: 1.5 }}>
                <h3>Comment ça marche ?</h3>
                <p>
                    - En mode <strong>HT → TTC</strong>, on calcule : <code>Prix TTC = Prix HT × (1 + TVA)</code>.<br />
                    - En mode <strong>TTC → HT</strong>, on calcule : <code>Prix HT = Prix TTC ÷ (1 + TVA)</code>.<br />
                    - La TVA est un pourcentage, donc on la divise par 100 pour obtenir un nombre décimal.<br />
                    - On vérifie que les valeurs entrées soient valides (nombre positif).<br />
                    - Le quiz permet de s’exercer avec des questions simples pour mieux comprendre.
                </p>
            </div>
        </div>
    );
}
