import React, { useState } from "react";
import { Input } from "../components/ui/input";
import { Button } from "../components/ui/button";
import { Card, CardContent } from "../components/ui/card";
import { useTranslation } from "react-i18next";
import { motion } from "framer-motion";
import {
    Newspaper,
    FileText,
    ScrollText,
    BookOpen,
    Globe2,
} from "lucide-react";

const tabs = [
    {
        key: "actualites",
        label: "{t(\"actualites\")}",
        icon: <Newspaper size={16} />,
        content: ["Nouvelle CNI", "Service en ligne", "Avis aux citoyens"],
    },
    {
        key: "etatCivil",
        label: "√âtat civil",
        icon: <FileText size={16} />,
        content: ["Acte de naissance", "Certificat de mariage", "D√©c√®s"],
    },
    {
        key: "demarches",
        label: "D√©marches",
        icon: <ScrollText size={16} />,
        content: ["Passeport", "Carte d'identit√©", "Logement"],
    },
    {
        key: "fiches",
        label: "Fiches pratiques",
        icon: <BookOpen size={16} />,
        content: ["Cr√©er un compte Amana", "S'inscrire √† l'universit√©"],
    },
    {
        key: "etranger",
        label: "√âtranger",
        icon: <Globe2 size={16} />,
        content: ["Consulat", "Retour d√©finitif", "Renouvellement passeport"],
    },
];

export default function HomePage() {
    const { t, i18n } = useTranslation();
    const [activeTab, setActiveTab] = useState("accueil");

    const currentTab = tabs.find((tab) => tab.key === activeTab);

    return (
        <div className="min-h-screen flex flex-col bg-gray-50 text-gray-900 font-sans">
            {/* HEADER */}
            <header className="bg-white shadow p-4 md:p-6 flex flex-col md:flex-row justify-between items-center gap-6">
                <div className="flex items-center gap-4">
                    <img src="/logo.svg" alt="Administration DZ" className="h-10" />
                    <h1 className="text-2xl font-bold text-green-700">{t("title")}</h1>
                </div>

                <div className="flex w-full max-w-md border rounded-lg overflow-hidden">
                    <Input
                        placeholder={t("searchPlaceholder")}
                        className="flex-grow px-4 py-2"
                    />
                    <Button className="bg-green-600 text-white hover:bg-green-700 px-4 py-2">
                        üîç
                    </Button>
                </div>

                <div className="flex gap-2">
                    <Button
                        onClick={() => i18n.changeLanguage("fr")}
                        className="bg-gray-200 hover:bg-gray-300 px-4"
                    >
                        Fran√ßais
                    </Button>
                    <Button
                        onClick={() => i18n.changeLanguage("ar")}
                        className="bg-gray-200 hover:bg-gray-300 px-4"
                    >
                        ÿßŸÑÿπÿ±ÿ®Ÿäÿ©
                    </Button>
                </div>
            </header>

            {/* NAVIGATION */}
            <nav className="bg-white border-b px-2 md:px-6 py-3 flex overflow-x-auto gap-2 md:gap-4 whitespace-nowrap">
                <button
                    onClick={() => setActiveTab("accueil")}
                    className={`px-4 py-2 rounded-md font-medium transition ${
                        activeTab === "accueil"
                            ? "bg-green-700 text-white"
                            : "text-gray-800 hover:bg-gray-100"
                    }`}
                >
                    Accueil
                </button>

                {tabs.map((tab) => (
                    <button
                        key={tab.key}
                        onClick={() => setActiveTab(tab.key)}
                        className={`flex items-center gap-2 px-4 py-2 rounded-md font-medium transition ${
                            activeTab === tab.key
                                ? "bg-green-700 text-white"
                                : "text-gray-800 hover:bg-gray-100"
                        }`}
                    >
                        {tab.icon}
                        {tab.label}
                    </button>
                ))}
            </nav>

            {/* SECTION ACCUEIL */}
            {activeTab === "accueil" && (
                <section className="p-8 text-center">
                    <h2 className="text-3xl font-bold text-gray-800 mb-4">
                        Bienvenue sur Administration DZ
                    </h2>
                    <p className="text-lg text-gray-600 mb-6">
                        Acc√©dez rapidement aux informations administratives alg√©riennes
                        gr√¢ce √† un site clair et moderne.
                    </p>
                    <div className="grid grid-cols-2 md:grid-cols-3 gap-6">
                        {tabs.map((tab) => (
                            <motion.div
                                key={tab.key}
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: 0.05 }}
                            >
                                <Card className="hover:shadow-xl transition-all">
                                    <CardContent className="text-center py-6">
                                        <h3 className="text-xl font-semibold text-gray-800">
                                            {tab.label}
                                        </h3>
                                        <Button
                                            onClick={() => setActiveTab(tab.key)}
                                            className="mt-4 bg-green-600 hover:bg-green-700 text-white"
                                        >
                                            Explorer
                                        </Button>
                                    </CardContent>
                                </Card>
                            </motion.div>
                        ))}
                    </div>
                </section>
            )}

            {/* SECTION ACTIVE */}
            {activeTab !== "accueil" && currentTab && (
                <main className="flex-grow p-8">
                    <motion.h2
                        className="text-2xl font-bold text-green-700 mb-4"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                    >
                        {currentTab.label}
                    </motion.h2>
                    <motion.ul
                        className="list-disc pl-6 space-y-3 text-gray-700"
                        initial={{ opacity: 0, x: -10 }}
                        animate={{ opacity: 1, x: 0 }}
                    >
                        {currentTab.content.map((item, idx) => (
                            <li key={idx} className="hover:text-green-600">
                                {item}
                            </li>
                        ))}
                    </motion.ul>
                </main>
            )}

            {/* FOOTER */}
            <footer className="text-center text-sm text-gray-600 py-6 border-t bg-white">
                <p>{t("footer")}</p>
            </footer>
        </div>
    );

// {t("searchPlaceholder")}
// {t("footer")}
// {t("actualites")}
// {t("etatCivil")}
// {t("demarches")}
// {t("fiches")}
// {t("etranger")}
//
//     "footer": "¬© 2025 Administration DZ - Tous droits r√©serv√©s",
//     "actualites": "Actualit√©s",
//     "etatCivil": "√âtat civil",
//     "demarches": "D√©marches",
//     "fiches": "Fiches pratiques",
//     "etranger": "√âtranger",
//     "welcomeMessage": "Bienvenue sur Administration DZ",
//     "welcomeDescription": "Acc√©dez rapidement aux informations administratives alg√©riennes gr√¢ce √† un site clair et moderne.",
//     "explorer": "Explorer"
}
