import React, { useState } from 'react';

const goalPosition = { x: 2, y: 0 }; // Objectif en haut √† droite (ligne 0, colonne 2)
const gridSize = 3;

export default function FirstAlgorithmGame() {
    const [code, setCode] = useState('');
    const [position, setPosition] = useState({ x: 0, y: 2 }); // D√©part en bas √† gauche
    const [direction, setDirection] = useState('haut'); // haut, bas, gauche, droite
    const [message, setMessage] = useState('');

    function resetGame() {
        setPosition({ x: 0, y: 2 });
        setDirection('haut');
        setMessage('');
    }

    function runCode() {
        resetGame();
        const commands = code.trim().split('\n').map(c => c.trim());
        let pos = { ...position };
        let dir = direction;

        for (let cmd of commands) {
            if (cmd === 'avancer()') {
                if (dir === 'haut') pos.y -= 1;
                if (dir === 'bas') pos.y += 1;
                if (dir === 'gauche') pos.x -= 1;
                if (dir === 'droite') pos.x += 1;
            } else if (cmd === 'tournerDroite()') {
                dir = tournerDroite(dir);
            } else if (cmd === 'tournerGauche()') {
                dir = tournerGauche(dir);
            } else {
                setMessage(`Commande inconnue : ${cmd}`);
                return;
            }

            // V√©rifie que le robot ne sort pas de la grille
            if (pos.x < 0 || pos.x >= gridSize || pos.y < 0 || pos.y >= gridSize) {
                setMessage("‚ùå Le robot est sorti de la grille !");
                return;
            }
        }

        if (pos.x === goalPosition.x && pos.y === goalPosition.y) {
            setMessage("üéâ Bravo ! Le robot est arriv√© √† destination !");
        } else {
            setMessage("üôà Le robot n'est pas encore arriv√©. R√©essaie !");
        }

        setPosition(pos);
        setDirection(dir);
    }

    function tournerDroite(dir) {
        const ordre = ['haut', 'droite', 'bas', 'gauche'];
        return ordre[(ordre.indexOf(dir) + 1) % 4];
    }

    function tournerGauche(dir) {
        const ordre = ['haut', 'gauche', 'bas', 'droite'];
        return ordre[(ordre.indexOf(dir) + 1) % 4];
    }

    return (
        <div className="max-w-xl mx-auto p-6 space-y-4 text-gray-800">
            <h1 className="text-2xl font-bold">ü§ñ √âcris ton premier algorithme</h1>
            <p>Commande le robot pour atteindre la üö© en haut √† droite. Il commence en bas √† gauche.</p>
            <p>Utilise les commandes suivantes :</p>
            <ul className="list-disc list-inside text-sm ml-4">
                <li><code>avancer()</code></li>
                <li><code>tournerDroite()</code></li>
                <li><code>tournerGauche()</code></li>
            </ul>

            <p className="italic text-gray-600 text-sm mb-2">
                √âcris une commande par ligne, par exemple :<br />
                <code>avancer()</code><br />
                <code>tournerDroite()</code><br />
                <code>avancer()</code>
            </p>

            <textarea
                rows={6}
                value={code}
                onChange={(e) => setCode(e.target.value)}
                className="w-full border rounded p-2 font-mono"
                placeholder={`avancer()\navancer()\ntournerDroite()\navancer()`}
            />

            <button
                onClick={runCode}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
                Lancer
            </button>

            <div className="text-lg mt-4 font-semibold">{message}</div>
        </div>
    );
}
